// 重设RoleDetailfunction GameConnection::RoleDetailReset(%this){  %this.RealStrengthConsume = 0;  %this.RealAcdAvoid = 0;  %this.RealMovement = 0;  %this.RealInterestRate = 0;  	echo("RAC: Role Detail Data Reset Done");}// 周期性调用以更新function GameConnection::RoleDetailUpdate(%this){	// 更新RealMovement	%m = $ConstBaseMovement;	%factor = 1.00;	switch(%this.LevelStrength)	{		case 1:			%factor -= 0.20;			break;		case 3:			%factor += 0.20;			break;	}	%m *= %factor;	%this.RealMovement = %m;		// 更新RealAcdAvoid	%a = $ConstBaseAcdAvoid;	%a +=  %this.RoleInterest * $ConstInterestIncreaseA;	%this.RealAckAvoid = %a;		// 更新RealStrengthConsume	%sc = $ConstBaseStrengthConsume;	%factor = 1.00;	if(%this.RoleCarry > $ConstFineCarry)		%factor += 0.20;	%this.RealStrengthConsume = %sc * %factor;		// 更新RealInterestRate	%this.RealInterestRate = $ConstBaseInterestRate;}					