function GameConnection::EnvDetailReset(%this){	%this.ESWear = 0;	%this.ESRain = 0;	%this.ESSweat = 0;		echo("RAC: Environment Detail Data Reset Done");}//function GameConnection::EnvDetailUpdate(%this){	// 更新ESWear	%wear = (30.0 - %this.GetGETempV()) / 40.0;	%this.ESWear = %wear;		// 更新ESRain	%this.ESRain = %this.GetGERainV();		// 更新ESSweat	%this.ESSweat = %this.GetGEDampV();}