// 初始化全局环境function GameConnection::GloEnvReset(%this){	%this.GETempN = "适宜";	%this.GEDampN = "正常";	%this.GERainN = "无";	%this.GELightN = "适度";		echo("RAC: Global Environment Data Reset Done");}// 命名环境描述取值function GameConnection::GetGETempV(%this){	%vTemp = 0.00;	switch(%this.GETempN)	{		case "寒冷":			%vTemp = 0;			break;		case "适宜":			%vTemp = 15;			break;		case "炎热":			%vTemp = 25;			break;		default:			%vTemp = 0;			break;	}		return %vTemp;}function GameConnection::GetGEDampV(%this){	%vAD = 0.00;	switch(%this.GEDampN)	{		case "干燥":			%vAD = 0;			break;		case "正常":			%vAD = 0.20;			break;		case "潮湿":			%vAD = 0.40;			break;	}	return %vAD;}function GameConnection::GetGERainV(%this){	%vRP = 0.00;	switch(%this.GERainN)	{		case "无":			%vRP = 0;			break;		case "小雨":			%vRP = 0.20;			break;		case "中雨":			%vRP = 0.50;			break;		case "暴雨":			%vRP = 0.70;			break;		case "雨夹雪":			%vRP = 0.40;			break;		case "中雪":			%vRP = 0.20;			break;		case "暴雪":			%vRP = 0.10;			break;		default:			%vRP = 0;			break;	}	return %vRP;}function GameConnection::GetGELightPowerV(%vLP){	// ...}