// 局部环境初始化function GameConnection::PartEnvReset(%client){	%client.PEName					 	= "未命名";	%client.PENTerrain					= "平坦";	%client.PENVar						= "平凡无奇";	%client.PENVLT						= "需要适应";	%client.PEViewTime				= 0;		echo("RAC: Part Environment Data Reset Done");}// 将下面的方法声明为GameConnection(client)的成员// 因为他们需要的输入数据都来自GameConnection// 而且他们在GameConnection类中被使用[客户端不可见数值]function GameConnection::GetPEStrengthFactor(%this){	%pesf = 0.00;	switch(%this.PENTerrain)	{		case "平坦":			%pesf = 0.00;		case "光滑":			%pesf = 0.50;		case "凹凸":			%pesf = 0.10;		case "难行":			%pesf = 0.20;		default:			%pesf = 0.00;	}	return %pesf;}function GameConnection::GetPEInterestFactor(%this){	%peif = 0.00;	switch(%this.PENVar)	{		case "荒芜之地":			%peif = -0.20;		case "平凡无奇":			%peif = 0;		case "物种丰富":			%peif = 0.20;		case "盛产奇珍":				%peif = 0.40;		default:			%peif = 0;	}	return %peif;}function GameConnection::GetPEVarLeft(%this){	%maxVarLeft = 0;	switch(%this.PEVLT)	{		case "极易沉浸":			%maxValLeft = 70;		case "需要适应":			%maxValLeft = 100;		case "余音不绝":			%maxValLeft = 150;	}	 %pevl = %maxVarLeft - $PEViewTime;	 if(%pevl < 0) %pevl = 0;	 return %pevl;}